class Solution{
public:
    int numberOfAlternatingGroups(vector<int> &c, int k) {
        c.insert(c.end(), c.begin(), c.begin() + k--);
        int res = 0, n = c.size();
        vector<int> pre(n, 0);
        for (int i = 1; i < n; ++i)
        {
            pre[i] = pre[i] + pre[i - 1] + abs(c[i - 1] - c[i]);
        }
        for (int i = k + 1; i < n; i++)
        {
            res += (pre[i] - pre[i - k] == k);
        }
        return res;
    }
};
