class Solution {
    int findTransitions( vector<int>& c,int i,int n,int altCnt,int ans,int k) {
        while (i < n + k - 1) {
            if (c[(i - k) % n] != c[(i - k + 1) % n]) {
                altCnt--;
            }
            if (c[(i - 1) % n] != c[i % n]) {
                altCnt++;
            }
            if (altCnt == k - 1) {
                ans++;
            }
            i++;
        }
        return ans;
    }
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        int ans = 0;
        int altCnt = 0;
        int i = 0;
        while (i +1< k ) {
            if (c[i] != c[i + 1]) {
                altCnt++;
            }
            i++;
        }
        if (altCnt + 1 == k ) {
            ans++;
        }
        i = k;
        return findTransitions(c,i,n,altCnt,ans,k);   
    }
};
