class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < n - 1; i++){
            colors.push_back(colors[i]);
        }
        vector<int> pref(2 * n);
        for(int i = 1; i < 2 * n - 1; i++){
            pref[i] = (colors[i] != colors[i - 1]);
            pref[i] += pref[i - 1];
        }
        int ans = 0;
        for(int i = 0; i < n; i++){
            if(pref[i + k - 1] - pref[i] == k - 1) ans++;
        }
        return ans;
    }
};
