class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> suff(n, 1);
        for(int i = n - 2; i >= 0; i--){
            if (colors[i] != colors[i + 1]){
                suff[i] += suff[i + 1];
            }
        }

        int ans = 0;

        for(int i = 0; i < n; i++){
            if (suff[i] >= k) ans++;
        }

        if (colors[0] == colors[n - 1]) return ans;

        for(int i = n - k + 1; i < n; i++){
            if (suff[i] == (n - i) && suff[i] + suff[0] >= k) ans++;
        }

        return ans;
    }
};
