class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),ans=0;
        vector<int> p(n);
        p[0]=1;
        for(int i=1;i<n;i++)
        {
            if(a[i]!=a[i-1])p[i]=p[i-1]+1;
            else p[i]=1;
            cout << p[i] << " ";
            if(p[i]>=k)ans++;
        }
        if(a[0]!=a[n-1])
        {
            if(p[n-1]+1>=k)ans++;
            for(int i=1;i<k-1;i++)
            {
                if(a[i]==a[i-1])break;
                if(p[n-1]+i+1>=k)ans++;
            }
        }
        return ans;
    }
};
