class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int sz = c.size();
        int r = 0;
        vector<int> v(sz);
        for(int i=0;i<sz;i++) v[i]=(c[i]!=c[(i+1)%sz]);
        for(auto e : v) cout<<e<<" ";
        cout<<endl;
        int start = -1;
        for(int i=0;i<sz;i++)
        {
            if(v[i]==0)
            {
                start = i;
                break;
            }
        }
        int val = start;
        int pv = start+1;
        if(start == -1) return sz;
        else
        {
            for(int i=start+1;i<sz;i++)
            {
                if(v[i]==0)
                {
                    //cout<<i-pv<<endl;
                    if(i-pv>=k-1) r+=(i-pv-k+2);
                    pv=i+1;
                }
            }
            //cout<<sz-pv+val<<endl;
            if(sz-pv+val>=k-1)
            {
                cout<<"A"<<endl;
                r+=(sz-pv+val-k+2);
            }
        }
        return r;
    }
};
