class Solution {

    /**
     * @param Integer[] $colors
     * @param Integer $k
     * @return Integer
     */
    function numberOfAlternatingGroups($c, $k) {
         $n = count($c);
        for ($i = 0; $i < $n; $i++) {
            $c[] = $c[$i];
        }

        $ans = 0;
        $cnt = 1;

        for ($i = 1; $i < $n + $k - 1; $i++) {
            if ($c[$i] == $c[$i - 1]) {
                $cnt = 1;
            } else {
                $cnt++;
            }

            if ($cnt >= $k) {
                $ans++;
            }
        }

        return $ans;
    }
}
