class Solution { 
public:
    int n=0;
    int cnt=0;
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        n=colors.size();
        int l=1;
        int i=1;
        if(k==1) return n;
        while(i<n+k-1){
            int prev=i-1;
            int cur=i;
            if(prev>=n) prev-=n;
            if(cur>=n) cur-=n;
            if(colors[prev]!=colors[cur]){
                l++;
            }
            else{
                l=1;
            }
            if(l>=k) cnt++;
            i++;
        }
        return cnt;
    } 
};
