class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ct = 0, n = v.size();
        int i = 0, j = 0;
        while(i < n) {
            while(j - i + 1 < k && (j - i + 1 == 1 || v[j%n] != v[(j - 1 + n) % n]) ) j++;
            if(v[j % n] == v[(j - 1 + n) % n] ) i = j;
            else {
                if(j - i + 1 == k) ct++;
                i++;
            }
        }
        return ct;
    }
};
