class Solution {
public: 
   int ans=0;
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int len = 1,i=1; 
        if(k==1)
        return (int)colors.size();
        while (i<=colors.size()-2+k) { 
            int mid = i,l=i-1;
            if (l>=(int)colors.size()) 
                l-=colors.size();
            if (mid >=colors.size()) 
                mid-=colors.size();
            if (colors[mid] != colors[l]) 
                len++;
            else 
               len = 1; 
            if (len >= k) 
                ans++;  
            i=(i+1)/1;
        }
        return ans;
    }
};
