#include <vector>

class Solution {
public:
    int SolveCount(const std::vector<int>& v, int start, int end, int k) {
        int cnt = 1;
        int ans = 0;
        int n = v.size();
        int i = start + 1;

        while (i < end) {
            int ind = i % n;
            int prev_ind = (i - 1) % n;

            if (v[ind] != v[prev_ind]) {
                cnt++;
            } else {
                cnt = 1;
            }

            if (cnt >= k) {
                ans++;
            }

            i++;
        }

        return ans;
    }

    int numberOfAlternatingGroups(std::vector<int>& colors, int k) {
        int n = colors.size();
        int ans = SolveCount(colors, 0, n + k - 1, k);
        return ans;
    }
};
