class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        auto c=[&](int num){
            return (num+n)%n;
        };
        set<int>st;
        for(int i=0;i<(k-1);i++){
            if(colors[i]==colors[c(i+1)]){
                st.insert(i);
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(st.size()==0){
                ans++;
            }
            if(colors[c(i+k-1)]==colors[c(i+k)]){
                st.insert(c(i+k-1));
            }
            if(colors[c(i+1)]==colors[i]){
                st.erase(c(i));
            }
        }
        return ans;
    }
};
