class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& a, int k) {
    vector<int> b = a;
    for (int i = 0; i < k - 1; ++i) {
      b.push_back(a[i]);
    }

    int res = 0;
    b.push_back(-1);
    int cc = 0, ce = !b[0];
    for (auto x : b) {
      if (x >= 0 && x != ce) {
        ++cc;
        ce = !ce;
      } else {
        res += max(cc - k + 1, 0);
        cc = 1;
        ce = x;
      }
    }

    return res;
  }
};
