class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt=0;
        for(int i=0;i<k-1;i++)
        {
            if(colors[i]!=colors[i+1])
                cnt++;
        }
        int ans=0;
        if(cnt==k-1)
            ans++;
        for(int i=1;i<colors.size();i++)
        {
            if(colors[i]!=colors[i-1])
                cnt--;
            if(colors[(i+k-1)%colors.size()]!=colors[(i+k-2)%colors.size()])
                cnt++;
            if(cnt==k-1)
                ans++;
        }
        return ans;
    }
};
