class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int i=0,j=1,n=colors.length;
        int window=1;
        int count=0;
        while(i<n)
        {
            if(colors[j%n]!=colors[(j-1)%n])
            {
                window++;
            }
            else
            {
                window=1;
                i=j;
            }
            if(window==k)
            {
                count++;
                i++;
                window--;
            }
            j++;
        }
        return count;
    }
}
