class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for (int i = 0; i < n; ++i) {
            a.push_back(a[i]);
        }
        int res = 0;
        int maxlen = 1;
        for (int i = 1; i <= n - 1 + k - 1; ++i) {
            if (a[i] != a[i-1]) {
                ++maxlen;
            } else {
                maxlen = 1;
            }
            if (maxlen >= k) {
                ++res;
            }
        }
        return res;
    }
};
