class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans = 0;

        int n = colors.length;
        int cur = 0;
        for (int i = 0; i < colors.length + k - 2; i++) {
            if (colors[(i - 1 + n) % n] != colors[(i + n) % n])
                cur++;
            else {
                cur = 0;
            }
            if (cur >= k-1)
                ans++;
        }


        return ans;
    }
}
