class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& t, int k) {
        int n=t.size();
        vector<int>v(2*n);
        for(int i=0;i<n;i++){
            v[i]=t[i];
        }
        for(int i=n;i<2*n;i++){
            v[i]=t[i-n];
        }
        vector<int>dp(2*n,0);
        dp[0]=1;
        for(int i=1;i<v.size();i++){
            if(v[i]!=v[i-1]){
                dp[i]=dp[i-1]+1;
            }
            else{
                dp[i]=1;
            }
        }
        int ans=0;
        int i=k-1;
        int j=0;
        while(j<t.size() && i<dp.size()){
            int left = dp[i-k+1]-1;
            int right = dp[i];
            if((right-left)==k){
                ans++;
            }
            i++;
            j++;
        }
        return ans;
    }
};
