class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v=colors;
        int n=colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(v[i]);
        }
        int ans=0;
        int tail=0, head=-1, cnt=0;
        while(tail<n){
            while(head+1<2*n && ((head+1==tail || colors[head+1]!=colors[head]) && cnt<k)){
                head++;
                cnt++;
            }
            // cout<<head<<" "<<tail<<endl;
            if(cnt==k) {
                ans++;
            }
            if(head>=tail) {
                tail++;
                cnt--;
            }else{
                tail++;
                head=tail-1;
            }
        }
        return ans;
    }
};
