class Solution:
    def numberOfAlternatingGroups(self, v: List[int], k: int) -> int:
        n=len(v)
        if(n<k):
            return 0;
        temp=v;
        l = 0
        r = 1
        count=0
        while r < n + k and l < n:
            right = r
            rightMax = right % n  
            while v[rightMax] != v[rightMax - 1] and (right < (n + k - 1)):
                right += 1
                rightMax = right % n
            count+= max(right - l - k + 1, 0)
            r = l + 1
            l = right

        v.sort()
        temp.reverse()
        return count
