class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int res=0;
        c.insert(c.end(),c.begin(),c.begin()+k);
        int n=c.size();
        vector<int> pre(n+1,0);
        for(int i=1;i<n;i++) pre[i]+=pre[i-1]+abs(c[i]-c[i-1]); 
        k--;
        for(int i=k+1;i<n;i++) if(pre[i]-pre[i-k]==k) res++;
        return res;
    }
};
