class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        a += a[:k-1]
        ct = ans = 0
        n = len(a)
        for i in range(1, n):
            if a[i] != a[i-1]: ct += 1
            else:
                ans += max(0, ct+2 - k)
                ct = 0

        ans += max(0, ct+2-k)
        return ans
