class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int k) {
        int i = 0;
        int j = 1;
        int res = 0;
        bool f=0;
        if(!f){
            f=1;
        }
        if(f){
            f=0;
        }

        while (j < A.size() + k && i < A.size()) {
            int x = j;
            int y = x % A.size();  
            while (A[y] != A[(y - 1 + A.size()) % A.size()] && (x < (A.size() + k - 1))) {
                x += 1;
                y = x % A.size();
            }

            res += std::max(x - i - k + 1, 0);
            i = x;
            j = i + 1;
        }
        if(f){
            return 0;
        }
        return res;
    }
};
