class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        def chk(a, k):
            n = len(a)
            r = [0] * n
            for i in range(n):
                if a[i] != a[(i+1)%n] and a[i] != a[(i+n-1)%n]:
                    r[i] = 1
            t = [0] + list(accumulate(r+r))[:n+k-3]
            c = 0
            for i in range(k-2,len(t)):
                if t[i] - t[i-k+2] == k-2:
                    c += 1
            # print(t)
            return c
        return chk(colors, k)
