#ifndef dhruvtyagi18
#define dhruvtyagi18

#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& dhruv, int tyagi) {
        dhruv.insert(dhruv.end(), dhruv.begin(), dhruv.begin() + tyagi - 1);

        int tyagi_ji = countAlternatingGroups(dhruv, tyagi);
        return tyagi_ji;
    }

private:
    int countAlternatingGroups(vector<int>& dhruv, int tyagi) {
        int tyagi_ji = 0;
        int dhruv_tyagi = 1;

        for (int i = 1; i < dhruv.size(); ++i) {
            if (dhruv[i] != dhruv[i - 1]) {
                dhruv_tyagi++;
            } else {
                dhruv_tyagi = 1;
            }

            if (dhruv_tyagi >= tyagi) {
                tyagi_ji++;
            }
        }

        return tyagi_ji;
    }
};

#endif
