class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        vector<int> ind;
        for(int i=0;i<n-1;i++){
            if(a[i]==a[i+1]){
                ind.push_back(i);
            }
        }
        int ans=0;
        for(int i=0;i<=n-k;i++){
            int it=lower_bound(ind.begin(),ind.end(),i)-ind.begin();
            if(it==ind.size()) {ans++; continue;}
            if(ind[it]+1>i+k-1) ans++;
        }
        if(a[0]==a[n-1]){
            return ans;
        }
        // cout<<"ans: "<<ans<<endl;
        for(int i=n-k+1;i<n;i++){
            int j=i+k-1-n;
            if(ind.empty() || (ind[0]+1>j && (ind.back()<i))) ans++;
            // else cout<<j<<endl;
        }
        return ans;

    }
};
