class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int count = 0;

        if (k == 1) {
            return n;
        }

        int altLen = 1;
        int i = 1;
        while (i < n + k - 1) {
            int cur = i;
            int prv = i - 1;
            if (cur >= n) {
                cur -= n;
            }
            if (prv >= n) {
                prv -= n;
            }

            if (colors[cur] != colors[prv]) {
                ++altLen;
            } else {
                altLen = 1;
            }

            if (altLen >= k) {
                ++count;
            }

            ++i;
        }

        return count;
    }
}
