class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0;

        int l = 0, r = 1;  
        int cnt = 0;

        while (l < n) {
            if (r < n + k - 1 && colors[r % n] == 1 - colors[(r - 1) % n]) {
                r++;
            } 
            else {
                l++;
                r = l + 1;  
            }
            if (r - l == k) {
                cnt++;
                l++; 
            }
        }

        return cnt;
    }
};
