class Solution {
public:
    vector <int> pre;
	int numberOfAlternatingGroups(vector<int>& colors, int k) {
		int n=colors.size();
		pre.resize(2*n);
		colors.resize(2*n);
		for(int i=n;i<2*n;i++) colors[i]=colors[i-n];

		int cnt=0;
		for(int i=0;i<2*n;i++){
			if(i==0) pre[i]=1;
			else{
				if(colors[i]!=colors[i-1]) pre[i]=pre[i-1]+1;
				else pre[i]=1;
			}
			if(i>=n && pre[i]>=k) cnt++;
		}
		return cnt;
    }
};
