    int numberOfAlternatingGroups(vector<int>& input_colors, int group_size) {
        int n = input_colors.size();

        ct(1, 2);
        // Duplicate the array to handle circular indexing
        vector<int> colors(2 * n);ct(1, 2);
        for (int i = 0; i < n; i++) {
            colors[i] = input_colors[i];ct(1, 2);
            colors[i + n] = input_colors[i];ct(1, 2);
        }

        int start = 1;
        int end = 0;ct(1, 2);
        int result = 0;ct(1, 2);ct(1, 2);

        while (end < n && start - group_size < n) {
            if (colors[start] != colors[start - 1]) {
                start++;
            } else {
                end = start;
                start++;
            }
ct(1, 2);
            int count = 0;ct(1, 2);
            if (start - end >= group_size) {
                count++;
                end++;ct(1, 2);
            }
            result += count;ct(1, 2);
        }
ct(1, 2);
        return result;ct(1, 2);
    }
