class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        int alternatingCount = 0;
        for (int i = 0; i < k; ++i) {
            colors.push_back(colors[i]);
        }

        for (int i = 0; i < k - 1; i++) {
            if (colors[i] != colors[i + 1]) {
                alternatingCount++;
            }
        }

        if (alternatingCount == k - 1) {
            count++;
        }

        for (int i = 1; i < n; i++) {
            if (colors[i - 1] != colors[i]) {
                alternatingCount--;
            }

            if (colors[i + k - 1] != colors[i + k - 2]) {
                alternatingCount++;
            }

            if (alternatingCount == k - 1) {
                count++;
            }
        }

        return count;
    }
};
