class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans=0;

        if(k==1)
            return n;

        int alt = 1;
        int i = 1;
        while(i<n+k-1){
            int curr = i;
            int prev= i-1;

            if(curr >= n){
                curr -= n;
            }
            if(prev>=n){
                prev-=n;
            }

            if(colors[curr]!=colors[prev]){
                ++alt;
            }else{
                alt = 1;
            }

            if(alt >= k){
                ans++;
            }
            i++;
        }

        return ans;
    }
};
