class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
          int n=colors.size();
        // vector<int>v;
        int ans=0;
        // v.push_back(colors[n-1]);
        for(int i=0;i<n;i++)colors.push_back(colors[i]);
        int l=0,h=1,m=k-1;
        while(l<n){
            while(h<2*n&&m>0&&colors[h]!=colors[h-1]){h++;m--;}
            if(m==0){ans++;l++;;m=1;}
            else{
                l=h;m=k-1;h=l+1;
            }

        }
        return ans;

    }
};
