class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        unordered_set<int> st;
        int n=c.size();

        for(int i=1;i<n;i++){
            if(c[i]==c[i-1]) st.insert(i);
        }

       if(c[0]==c[n-1]) st.insert(0);

        int j=0;
        int ct=0;
        int ans=0;

        for(int i=0;;i++){

            if(j==n) break;

            int ind=i%n;

            if(st.find(ind)!=st.end()) ct++;

            if(i-j+1==k){
                if(st.find(j)!=st.end()) ct--;

                if(ct==0) ans++;
                j++;
            }
        }


        return ans;
    }
};
