class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        ans = 0
        stk = 0
        for i in range(1, n + k - 1):
            if colors[i % n] != colors[(i - 1) % n]:
                stk += 1
            else:
                stk = 0

            if stk >= k - 1:
                ans += 1

        return ans
