class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        # if n==k:
        #     if all(colors[i] != colors[(i+1)%n] for i in range(n)):
        #         return 1
        #     return 0
        ans = []

        i = 0

        while i<n:
            j = i+1

            while j<2*n and colors[j%n] == colors[i]:
                j+=1

            if j != i+1:
                ans.append([i, j-1])
            i = j
        if not len(ans):
            return n

        to = 0
        # print(ans)
        for i in range(len(ans)):
            j = (i+1)%len(ans)
            cto = 0
            if i>=j:
                cto = ans[j][0]+n-ans[i][-1]+1
            else:
                cto = ans[j][0] - ans[i][1]+1
            to += max(0, cto-k+1)
        return to
