class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if(k > n) return 0;
        int st1 = k / 2, st2 = (k+1) / 2;
        vector<vector<int>> a(n, vector<int> (2, 0));
        int i = 0, it = st1, sum = 0;
        while(it--){
            sum += colors[i];
            i = (i + 2) % n;
        }
        a[0][0] = sum;
        a[0][1] = sum;
        if(st2 != st1) a[0][1] += colors[i];
        i = 1, it = st1, sum = 0;
        while(it--){
            sum += colors[i];
            i = (i + 2) % n;
        }
        a[1][0] = sum;
        a[1][1] = sum;
        if(st2 != st1) a[1][1] += colors[i];
        for(int i=2; i<n; i++){
            a[i][0] = a[(i-2+n) % n][0] - colors[(i-2+n) % n];
            int ind = ((i + (st1-1)*2)%n + n)%n;
            a[i][0] += colors[ind];
            a[i][1] = a[(i-2+n) % n][1] - colors[(i-2+n) % n];
            ind = ((i + (st2-1)*2)%n + n)%n;
            a[i][1] += colors[ind];
        }
        int ans = 0;
        for(int i=0; i<n; i++){
            int x = a[i][1];
            int y = a[(i+1)%n][0];
            if(colors[i] == 1){
                if(x == st2 && y == 0){
                    ans++;
                }
            }
            else{
                if(y == st1 && x == 0){
                    ans++;
                }
            }
        }
        return ans;
    }
};
