class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,n=colors.size();
        int i=1,j=1,x=0;
        while(i<=n&&j<=n+k-3)
        {
            if((colors[j%n]!=colors[(j-1)%n])&&(colors[j%n]!=colors[(j+1)%n]))
            {
                j++;
                x++;
                if(x==k-2) 
                {
                    ans++;
                    i++;
                    x--;
                }
            }
            else
            {
                j++;
                i=j;
                x=0;
            }
        }
        return ans;
    }
};
