class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int notAlter = -1;
        for (int i = 0; i < n; i++) {
            if (colors[i] == colors[(i+1)%n]) {
                notAlter = i+1;
                break;
            }
        }
        if (notAlter == -1) {
            return n;
        }

        int ans = 0;
        int start = notAlter;
        int count = 1;
        for (int i = notAlter+1; i < notAlter + n; i++) {
            if (colors[i % n] != colors[(i-1) % n]) {
                count++;
            }
            else{
                if (count >= k) {
                    ans += count - k + 1;
                }
                start = i;
                count = 1;
            }
        }
        if (count >= k) {
            ans += count - k + 1;
        }

        return ans;
    }
}
