class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
       int n=colors.length;
       int consec=1;
       int count=0;
       for(int i=1;i<(n+k-1);i++){
        if(colors[(i%n)]!=colors[(i+1)%n]){
             consec++;
        }else{
            consec=1;
        }
        if(consec>=k){
            count++;
        }
       }
       return count;
    }
}
