class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k)
    {
         int n = v.size();
         vector<int> v1 = v;
        // reverse(v.begin(), v.end());

         for(int i = 0; i < min(k, n); i++) v1.push_back(v[i]);

         n = v1.size();
         int j = 0;
         int ans = 0;
         for(auto it : v1) cout << it <<" ";
         for(int i = 0; i < n - 1; i++)
         {
             if(v1[i] == v1[i + 1])
             {
                 int x = (i - j + 1);
                 x = (x - k + 1);
                 ans += max(0, x);
                 j = i + 1;
             }
         }

               int x = (n - j);
                 x = (x - k);
                 ans += max(0, x);
         return ans;
    }
};
