    int getMaxLen(vector<int> &nums, int i,int k,vector<int> cur, vector<vector<int>> &dp){
        if(i == nums.size()){
            // ans = max(ans ,cur.size());
            if(cur.size() < 2)return cur.size();
            int rem = (cur[0] + cur[1])%k;
            for(int i=2;i<cur.size()-1;i++){
                if((cur[i] + cur[i+1]) % k != rem)return 0;
            }
            return cur.size();
        }

        if(dp[cur.size()][i] != -1 )return dp[cur.size()][i];

        int a =0;
        if(cur.size()<2){
            cur.push_back(nums[i]);
            a = getMaxLen(nums,i+1,k,cur,dp);
            cur.pop_back();
        }

        int b=0;
        if( cur.size() >=2 and cur.size()%2 == 0){
            cur.push_back(nums[i]);
            b = getMaxLen(nums,i+1,k,cur,dp);
            cur.pop_back();

        }

        int c = 0;
        int n = cur.size() ;
        if(cur.size()>=3 and cur.size()%2 == 1){

            int rem = (cur[n-3] + cur[n-2])%k;

            if((nums[i] + cur[n-1])%k == rem){
                cur.push_back(nums[i]);
                c = getMaxLen(nums,i+1,k,cur,dp);
                cur.pop_back();
            }
        }

        int d = getMaxLen(nums,i+1,k,cur,dp);



        return dp[cur.size()][i] = max({a,b,c,d});
    }





