class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k);
        int n = colors.size(), ans = 0;
        map<string, int> mp;
        vector<int> pDiffs(n + 1, 0);
        for(int i = 1; i < n; i++)
        {
            pDiffs[i] += pDiffs[i - 1] + abs(colors[i] - colors[i - 1]);
        }
        k--;
        for(int i = k + 1; i < n; i++)
        {
            ans += (pDiffs[i] - pDiffs[i - k] == k);
        }
        return ans;
    }
};
