#include <vector>

class Solution {
public:
    int numberOfAlternatingGroups(std::vector<int>& arr, int k) {
        int numFound = 0;
        int i = 0;
        int j = 1;
        int n = arr.size();

        while (j < n + k && i < n) {
            int tempJ = j;
            int tempEnd = tempJ % n;

            while (arr[tempEnd] != arr[(tempEnd - 1 + n) % n] && (tempJ < n + k - 1)) {
                tempJ += 1;
                tempEnd = tempJ % n;
            }

            numFound += std::max(tempJ - i - k + 1, 0);
            i = tempJ;
            j = i + 1;
        }

        return numFound;
    }
};
