class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors + colors[:k-1]
        count = 0
        diff = sum(colors[i] != colors[i+1] for i in range(k-1))
        if diff == k-1:
            count += 1
        for i in range(1, n):
            diff -= colors[i-1] != colors[i]
            diff += colors[i+k-2] != colors[i+k-1]
            if diff == k-1:
                count += 1
        return count
