#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt=0;
        ll n=colors.size();
        ll i=1,l=1;
        ll len= n+k-1;
        while(i<len){

            ll p=i-1;
            if(p>=n) p-=n;

            ll c=i;
            if(c>=n) c-=n;

            if(colors[c]==colors[p]) l=1;
            else l++;

            if(k<=l) cnt++;

            i++;   
        }
        return cnt;
    }
};
