class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>a(2*n);
        for(int i=0;i<n;i++){
            a[i]=colors[i];
            a[i+n]=colors[i];
        }
        int alt=0;
        int ans=0;
        for(int i=0;i<k-1;i++){
            if(a[i]!=a[i+1]) alt++;
        }
        if(alt==k-1) ans++;
        for(int i=1;i<n;i++){
            if(a[i-1]!=a[i]) alt--;
            if(a[i+k-2]!=a[i+k-1]) alt++;
            if(alt==k-1) ans++;
        }
        return ans;
    }
};
