class Solution {
public:
    void fun(vector<int>& v, int &k,int &p,int &q){
        for(int i=1;i<v.size();i++){
            if(v[i]!=v[i-1]){
                q=q+1;

            }
            else{
                q=1;
            }
            if(q>=k){
                p++;
            }
        }
    }
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int p=0,q=1;
        v.insert(v.end(),v.begin(),v.begin()+k-1);
        fun(v,k,p,q);
        return p;
    }
};
