class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size(), alt = 0;
        queue<int> q;
        q.push(c[0]);
        for(int i = 1; i < k - 1; i++) {
            if(q.back() == c[i]) alt++;
            q.push(c[i]);
        }
        int ans = 0;
        for(int i = k - 1; i < n; i++) {
            if(q.back() == c[i]) alt++;
            q.push(c[i]);
            if(alt == 0) ans++;
            int f = q.front(); q.pop();
            if(f == q.front()) alt--;
        }
        for(int i = 0; i < k - 1; i++) {
            if(q.back() == c[i]) alt++;
            q.push(c[i]);
            if(alt == 0) ans++;
            int f = q.front(); q.pop();
            if(f == q.front()) alt--;
        }
        return ans;
    }
};
