public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.Length;
        int[] arr = new int[n*2];
        for (int i=0;i<arr.Length;i++) {
            arr[i] = colors[i%n];
        }
        int[] dp = new int[2*n];
        int ans = 0;
        for (int i=1;i-k+1<=n-1;i++) {
            if (arr[i] != arr[i-1]) {
                dp[i] = dp[i-1] + 1;
            } else {
                dp[i] = 0;
            }
            if (dp[i] >= k-1) {
                ans++;
            }
        }
        return ans;
    }
}
